apeMetadata":null},"Paragraph:4006bf29981d_14":{"__typename":"Paragraph","id":"4006bf29981d_14","name":"3fe6","type":"P","href":null,"layout":null,"metadata":null,"text":"Alternatively, you can also use Java Util.","hasDropCap":null,"dropCapImage":null,"markups":[{"__typename":"Markup","type":"STRONG","start":32,"end":41,"href":null,"anchorType":null,"userId":null,"linkMetadata":null}],"codeBlockMetadata":null,"iframe":null,"mixtapeMetadata":null},"Paragraph:4006bf29981d_15":{"__typename":"Paragraph","id":"4006bf29981d_15","name":"6d82","type":"PRE","href":null,"layout":null,"metadata":null,"text":"if (Objects.isNull(myObject)) {\n  \u002F\u002F Null or empty\n}\n\nif (Objects.nonNull(myObject)) {\n  \u002F\u002F Not null or empty\n}","hasDropCap":null,"dropCapImage":null,"markups":[],"codeBlockMetadata":{"__typename":"CodeBlockMetadata","mode":"EXPLICIT","lang":"java"},"iframe":null,"mixtapeMetadata":null},"Paragraph:4006bf29981d_16":{"__typename":"Paragraph","id":"4006bf29981d_16","name":"ebb3","type":"P","href":null,"layout":null,"metadata":null,"text":"If you want to assign a default value to an object in case it is null, you can use the defaultIfNull method of the ObjectUtils class.","hasDropCap":null,"dropCapImage":null,"markups":[{"__typename":"Markup","type":"STRONG","start":87,"end":101,"href":null,"anchorType":null,"userId":null,"linkMetadata":null},{"__typename":"Markup","type":"STRONG","start":115,"end":127,"href":null,"anchorType":null,"userId":null,"linkMetadata":null}],"codeBlockMetadata":null,"iframe":null,"mixtapeMetadata":null},"Paragraph:4006bf29981d_17":{"__typename":"Paragraph","id":"4006bf29981d_17","name":"5d97","type":"PRE","href":null,"layout":null,"metadata":null,"text":"Integer age = ObjectUtils.defaultIfNull(inputAge, 18);","hasDropCap":null,"dropCapImage":null,"markups":[],"codeBlockMetadata":{"__typename":"CodeBlockMetadata","mode":"EXPLICIT","lang":"java"},"iframe":null,"mixtapeMetadata":null},"Paragraph:4006bf29981d_18":{"__typename":